    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        //Declare variables
        String sen = "";
        int de,i = 0;
        //Get the decimal number form user
        de = Integer.parseInt(decimal1.getText());
        //Describe the variables
        int binary [] = new int [de]; 
        //Calculate
        while(de != 0){    
        binary[i] = de % 2;    
        de = de/2;  
        i++;
     }    
        //Constitute the binary in a new String
        for(int j = i - 1;j >= 0;j--){  
            sen = sen + binary[j];
        }
        answer1.setText(sen);  
    }//GEN-LAST:event_jButton1ActionPerformed

