    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        //Declare variables
        String bin;
        //Get binary from user
        bin = binary.getText();
        //Convert binary to decimal
        long num2 = Long.parseLong(bin,2);
        //Display
        answer2.setText(String.valueOf(num2));
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        //Declare variables
        String hexadecimal = "";
        char word;
        //Get the decimal number from user
        int decimal = Integer.parseInt(decimal2.getText());
        //Convert decimal to hexadecimal
        while (decimal != 0){
            int value = decimal % 16;
            if (value <= 9 && value >= 0){
                word= (char)(value + '0');
            }
            else{
                word= (char)(value - 10 + 'A');
            }
            hexadecimal = word + hexadecimal;
            decimal = decimal / 16;
        }
        //Display
        answer3.setText(hexadecimal);
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        //Get the hexadecimal number from user
        String decimal4 = hexadecimal.getText();
        //Convert hexadecimal to decimal
        long dec = Long.parseLong(decimal4, 16);
        //Output
        answer4.setText(String.valueOf(dec));
    }//GEN-LAST:event_jButton4ActionPerformed

